function introScrollEffect(){1.3*$(this).scrollTop()<$(window).height()?$(".js-intro").css({opacity:1-parseInt($(this).scrollTop())/(parseInt($(".js-intro").css("height"))/1.8)}):$(".js-intro").css({opacity:0})}$(document).ready(function(){introScrollEffect(),$(".js-exp-item").tilt({scale:1.05,maxTilt:8,perspective:1e3,tiltSpeed:400})}),$(document).scroll(function(){introScrollEffect()});